-- ----------------------------
-- Table structure for mv_blog_post
-- ----------------------------
DROP TABLE IF EXISTS `mv_blog_post`;
CREATE TABLE `mv_blog_post`  (
  `post_id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(250) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `lead` varchar(500) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `keywords` text CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `created_by` int(10) UNSIGNED NOT NULL,
  `created_date` int(11) UNSIGNED NOT NULL,
  `updated_date` int(11) UNSIGNED NOT NULL DEFAULT 0,
  `updated_by` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `category_id` int(5) UNSIGNED NOT NULL,
  `cover_image` varchar(150) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `deleted` tinyint(1) UNSIGNED NOT NULL DEFAULT 0,
  PRIMARY KEY (`post_id`) USING BTREE,
  INDEX `category_id`(`category_id`) USING BTREE,
  INDEX `deleted`(`deleted`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 142 CHARACTER SET = utf8 COLLATE = utf8_bin ROW_FORMAT = Dynamic;

